import { useState } from 'react';

export default function useForm(getUsername) {

    const [input, setInput] = useState(getUsername());
    const [errorMsg, setErrorMsg] = useState({});

    const handleInputUser = e => {
        const { username, value } = e.target
        setInput({
            ...input,
            [username]: value
        })
    }

    return {
        input,
        setInput,
        errorMsg,
        setErrorMsg,
        handleInputUser
    }
}
