import { useState } from 'react';

export default function useForm(getFreshModelObject, getUsername) {

    const [values, setValues] = useState(getFreshModelObject());
    const [errors, setErrors] = useState({});
    const [inputs, setInputs] = useState(getUsername());
    const [errorMsg, setErrorMsg] = useState({});

    const handleInputUsername = e => {
        const { username, input } = e.target
        setInputs({
            ...inputs,
            [username]: input
        })
    }

    const handleInputChange = e => {
        const { name, value } = e.target
        setValues({
            ...values,
            [name]: value
        })
    }


    return {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange,
        inputs,
        setInputs,
        errorMsg,
        setErrorMsg,
        handleInputUsername
    }
}
