import { useState } from 'react';

export default function useFormUsername(getUsername) {

    const [inputs, setInputs] = useState(getUsername());
    const [errorMsg, setErrorMsg] = useState({});

    const handleInputUsername = e => {
        const { username, input } = e.target
        setInputs({
            ...inputs,
            [username]: input
        })
    }

    return {
        inputs,
        setInputs,
        errorMsg,
        setErrorMsg,
        handleInputUsername
    }
}
